import { useState } from "react"

export const SimpleForm = () => {

    const [formState, setFormState] = useState({
        username: 'Nano',
        email: 'rodrigueznano404@gmail.com'
    });

    const {username, email} = formState;

    const onInputChange = ({target}) => {
      console.log(target.name)
    }

  return (
    <div>
        <hr/>
        <h1>Formulario Simple</h1>

        <input type="text" className="form-control" placeholder="Username" name="username" value={username} onChange={event => onInputChange(event)}></input>

        <input type="email" className="form-control mt-3" placeholder="email@gmail.com" name="email" value={email} onChange={event => onInputChange(event)}></input>

    </div>
  )
}
