import { useCounter } from "../hooks/useCounter";
import { useFetch } from "../hooks/useFetch";

export const MultipleCustomHooks = () => {
  const { counter, increment, decrement } = useCounter(1);

  const { data, isLoading } = useFetch(
    `https://www.breakingbadapi.com/api/quotes/${counter}`
  );

  const { author, quote } = !!data && data[0];

  return (
    <div>
      <hr />
      <h1>BreakingBad Quotes</h1>

      {isLoading ? (
        <div className="alert alert-info text-center">Loading...</div>
      ) : (
        <blockquote className="blockquote text-end me-5">
          <p className="mb-1">{quote}</p>
          <footer className="blockquote-footer">{author}</footer>
        </blockquote>
      )}

      <button className="btn btn-primary ms-3" onClick={() => increment(1)}>
        Next Quote
      </button>

      {
        counter != 1 ? (
        <button className="btn btn-primary ms-3" onClick={() => decrement(1)}>
            Previous Quote
        </button>

        ) : (
            <button className="btn btn-primary ms-3 d-none" onClick={() => decrement(1)}>
            Previous Quote
        </button>
        )
      }

    </div>
  );
};
