import { useState } from "react"

export const CounterApp = () => {

    const [counter, setCounter] = useState(10)

  return (
    <div>
        <h1 className="ms-2">Counter:{counter}</h1>

        <hr/>

        <button className="btn btn-primary ms-5" onClick={() => setCounter( counter + 1)}>+ 1</button>
    </div>
  )
}
